offensive_stats &lt;- nflreadr::load_player_stats(2025)


pats_receiving &lt;- offensive_stats |&gt;
  filter(team == "NE") |&gt;
  filter(position %in% c("WR","TE","RB")) |&gt;
   group_by(position) |&gt;
  summarise(avg_yards = mean(receiving_yards),
              se = sd(receiving_yards) / sqrt(n())) |&gt;
  mutate(se_lb = avg_yards - se,
         se_ub = avg_yards + se) |&gt;
  mutate(position = fct_reorder(position, avg_yards))

ggplot(data = pats_receiving, aes(x = position, y = avg_yards)) +
  geom_point() +
  geom_errorbar(aes(ymin = se_lb, ymax = se_ub)) +
   theme_minimal() +
  coord_flip()









pats_receiving_players &lt;- offensive_stats |&gt;
  filter(team == "NE") |&gt;
   filter(position %in% c("WR","TE","RB")) |&gt;
  group_by(player_display_name) |&gt;
    summarise(avg_yards = mean(receiving_yards),
              se = sd(receiving_yards) / sqrt(n())) |&gt;
  mutate(se_lb = avg_yards - se,
         se_ub = avg_yards + se) |&gt;
  mutate(position = fct_reorder(player_display_name, avg_yards))

ggplot(data = pats_receiving_players, aes(x = player_display_name, y = avg_yards)) +
  geom_point() +
  geom_errorbar(aes(ymin = se_lb, ymax = se_ub)) +
   theme_minimal() +
  coord_flip()









